const $=document.querySelector.bind(document);const $$=document.querySelectorAll.bind(document);const tabs=$$(".tab-btn");const contents=$$(".content-desc");tabs.forEach(btn=>{btn.onclick=function(e){tabs.forEach(btn=>btn.classList.remove("active"));btn.classList.add("active");const title=$(btn.dataset.id);const line=$(".line");contents.forEach(content=>content.classList.remove("active"));title.classList.add("active");line.style.width=e.target.offsetWidth+"px";line.style.left=e.target.offsetLeft+"px"}});